import { CacheMoudle } from "../src/CacheMoudle"
let cc = CacheMoudle.createCache("cc", 10)

describe("cache", function () {
    before(function (done) {
        CacheMoudle.init();
        done()
    })

    it("set", function (done) {
        cc.set("name", "chenkai", 1);
        // (cc as any)._checkData();
        cc.get("name")
        done()
    })

    it("get", function (done) {
        console.log(cc.get("name"))
        done()
    })

    it("del", function (done) {
        cc.del("name")
        done()
    })

    after(function () {
        return new Promise<void>(async function (r) {
            r()
            setTimeout(function () {
                process.exit(0)
            }, 1000)
        })

    })
})